let initialState = {
  input: ''
}

const todos = (state = initialState, action) => {
  console.log('------------------------------------');
  console.log(action);
  console.log('------------------------------------');
    switch (action.type) {
      case 'ADD_TO_INPUT':
        return Object.assign({}, state, {input: action.val})
      default:
        return state
    }
  }

  export default todos
