import React from 'react';
import { connect } from 'react-redux';
import store from '../store';
import './Input.css';

let mapStateToProps = (state) => {
    input: state.input;
}

let Input = ({children}) => {
  return (
    <input className="input">
        {children}
    </input>
  );
};

export default connect(
    mapStateToProps
)(Input);
