let initialState = {
  input: '',
  previousNumber: '',
  currentNumber: '',
  operator: ''
}

const calculator = (state = initialState, action) => {
    switch (action.type) {
      case 'ADD_TO_INPUT':
        return Object.assign({}, state, {input: state.input + action.val})
      case 'CLEAR_INPUT':
      return Object.assign({}, state, {input: ''})
      default:
        return state
    }
  }

  export default calculator
