let state = {

}

const todos = (state = [], action) => {
    switch (action.type) {
      case 'ADD_TO_INPUT':
        return Object.assign({}, state, {val: action.val})
      case 'TOGGLE_TODO':
        return state.map(todo =>
          todo.id === action.id ? { ...todo, completed: !todo.completed } : todo
        )
      default:
        return state
    }
  }

  export default todos
