import React from 'react';
import { connect } from 'react-redux';
import store from '../store';
import { AddToInput, ClearInput, Add } from '../actions/action'; 

import './App.css';
import Input from './Input';
import Button from './Button';
import ClearButton from './ClearButton' ;

const mapStateToProps = function (state){
  return {
      input: state.calculator.input
  }
}

const addToInput = (val) => {
  store.dispatch(AddToInput(val))
}
const clearInput = () => {
  store.dispatch(ClearInput())
}

const addition = (val) => {
  store.dispatch(Add(val))
}

let  App = (props) => {
  return (
    <div className="app">
      <div className="block-component">
      <div className="row">
        <Input>{props.input}</Input>
      </div>
        <div className='row'>
          <Button handleClick={addToInput}>7</Button>
          <Button handleClick={addToInput}>8</Button>
          <Button handleClick={addToInput}>9</Button>
          <Button >/</Button>
        </div>
        <div className='row'>
          <Button handleClick={addToInput}>4</Button>
          <Button handleClick={addToInput}>5</Button>
          <Button handleClick={addToInput}>6</Button>
          <Button >*</Button>
        </div>
        <div className='row'>
          <Button handleClick={addToInput}>1</Button>
          <Button handleClick={addToInput}>2</Button>
          <Button handleClick={addToInput}>3</Button>
          <Button handleClick={addition}>+</Button>
        </div>
        <div className='row'>
          <Button>.</Button>
          <Button>0</Button>
          <Button>=</Button>
          <Button>-</Button>
        </div>
        <div className="row">
          <ClearButton handleClick={clearInput}>Clear</ClearButton>
        </div>

      </div>
    </div>
  );
};

export default connect(
  mapStateToProps
)(App);
